parameters.functionCode:
const content = $input.first().json.content[0].text;
const sections = content.split(/## /);
return [{
  json: {
    lore: sections.find(s => s.startsWith('Lore')) || '',
    social: sections.find(s => s.startsWith('Social')) || '',
    challenge: sections.find(s => s.startsWith('Community')) || '',
    artPrompt: sections.find(s => s.startsWith('Art')) || '',
    fullContent: content,
    date: new Date().toISOString().split('T')[0]
  }
}];
